    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
//<editor-fold defaultstate="collapsed" desc="contraseña">
        StringBuffer sb = new StringBuffer();
        char passw[] = txtPass2.getPassword();
        for (int j = 0; j < passw.length; j++) {
            sb.append(passw[j]);
        }
        String password1 = sb.toString();

//</editor-fold>
        String puesto = txtPuesto.getText();
        int cont = 0;
        for (int i = 0; i < txtPuesto.getText().length(); i++) {
            if (Character.isDigit(puesto.charAt(i))) {
                cont++;
            }
        }

        if (password1.equals("")
                || txtDir.getText().equals("")
                || txtNombre.getText().equals("")
                || txtTlf.getText().equals("")
                || txtUsuario.getText().equals("")
                || txtPuesto.getText().equals("")) {
            JOptionPane.showMessageDialog(null, "Faltan campos por rellenar", "Error", JOptionPane.ERROR_MESSAGE);
        } else if (!txtUsuario.getText().contains("admin") || !password1.contains("2019")) {
            JOptionPane.showMessageDialog(null, "Formato de nombre o contraseña incorrecto", "Error", JOptionPane.ERROR_MESSAGE);
        } else if (cont != txtPuesto.getText().length()) {
            JOptionPane.showMessageDialog(null, "Puesto incorrecto (numérico)", "Error", JOptionPane.ERROR_MESSAGE);
        } else {
            String sexo = "";
            if (cmbSeo.getSelectedItem().equals("Masculino")) {
                sexo="M";
            } else if (cmbSeo.getSelectedItem().equals("Femenino")) {
                sexo="F";

            }
            emple.setIdEmpleado(txtUsuario.getText());
            emple.setNombre(txtNombre.getText());
            emple.setTelefono(txtTlf.getText());
            emple.setDireccion(txtDir.getText());
            emple.setPuesto(Integer.parseInt(txtPuesto.getText()));

            // realiza la conexión con la BD
            ConectorBD conexion = new ConectorBD();
            String sql = "insert into empleados values( '" + emple.getIdEmpleado()
                    + "','" + emple.getNombre()+sexo
                    + "','" + emple.getTelefono()
                    + "','" + emple.getDireccion()
                    + "','" + emple.getPuesto()
                    + "')";
            String sql2 = "insert into persona values( '" + emple.getIdEmpleado()
                    + "','" + password1
                    + "')";
// recoge el Stament devuelto por la conexión
            try (Statement st = conexion.connec()) {
// para Insertar, Borrar o Modificar. También se puede usar el métod st.execute (sql);
                st.executeUpdate(sql2);
                st.executeUpdate(sql);

                JOptionPane.showMessageDialog(null, "Operación realizada con éxito", "Sentencia SQL",
                        JOptionPane.INFORMATION_MESSAGE);
                txtUsuario.setText("");
                txtDir.setText("");
                txtPass.setText("");
                txtPass2.setText("");
                txtTlf.setText("");
                txtPuesto.setText("");
                txtNombre.setText("");
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "Error de operación " + e.getMessage(), "Sentencia SQL", JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

