    private void txtPass2FocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_txtPass2FocusLost
//<editor-fold defaultstate="collapsed" desc="Contraseñas">
        StringBuffer sb = new StringBuffer();
        char passw[] = txtPass.getPassword();
        for (int j = 0; j < passw.length; j++) {
            sb.append(passw[j]);
        }
        String password1 = sb.toString();

        StringBuffer sb2 = new StringBuffer();
        char passw2[] = txtPass2.getPassword();
        for (int f = 0; f < passw2.length; f++) {
            sb2.append(passw2[f]);
        }
        String password2 = sb2.toString();

//</editor-fold>
        if (!password1.equals(password2)) {
            txtPass.setText("");
            txtPass2.setText("");
        }        
    }//GEN-LAST:event_txtPass2FocusLost


